.anime {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(13rem, 14rem));
  justify-content: center;
  gap: 2em;
}

.grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(12rem, 14rem));
  gap: 2em;
  --spacer: 2rem;

  @media (min-width: 31.25em) {
    grid-template-columns: repeat(auto-fit, minmax(11rem, 1fr));
  }

  @media (min-width: 50em) {
    grid-template-columns: repeat(auto-fit, minmax(12rem, 1fr));
  }
}

.anime-card {
  position: relative;

  .heart {
    position: absolute;
    z-index: 2;
    top: 0.5em;
    right: 0.5em;
    visibility: hidden;
    opacity: 0;
    transition: opacity var(--animation-350);
  }

  &:hover .heart,
  &:focus .heart {
    visibility: visible;
    opacity: 1;
  }

  .heart path {
    transition: fill var(--animation-350);
  }

  .heart:hover path,
  .heart:focus path {
    fill: var(--clr-secondary);
  }

  .image {
    border-radius: var(--border-radius);
    overflow: hidden;
    height: 19.875rem;
  }

  .poster {
    height: 100%;
    width: 100%;
    object-fit: cover;
    transform: scale(1);
    transition: transform var(--animation-350);
  }

  &:hover .poster,
  &:focus .poster,
  .poster:hover,
  .poster:focus {
    transform: scale(1.05);
  }

  .info {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }

  .title {
    text-align: left;
  }

  .rating {
    display: flex;
    align-items: center;
    margin-left: 0.5em;
  }

  .rating > * + * {
    margin-left: 0.1em;
  }

  .name {
    transition: color var(--animation-350);
  }

  .name:hover,
  .name:focus {
    color: var(--clr-accent);
  }
}

