  --flow-space: 1.5rem;
  text-align: center;

  .hero-image {
    align-self: center;
  }

  .hero-image img {
    max-height: 22rem;
  }

  .companies {
    justify-content: center;
  }

  @media (min-width: 50em) {
    display: flex;
    align-items: center;
    text-align: left;

    .split {
      flex-wrap: wrap;
      justify-content: space-between;
    }

    .info {
      flex-basis: 55%;
    }

    .hero-image {
      flex-basis: 40%;
    }

