.devices {
  .image {
    margin-left: auto;
    margin-right: auto;
  }

  .companies {
    justify-content: center;
  }

  @media (min-width: 50em) {
    header {
      --flow-space: 2rem;
    }
  }
}

// info-page

.anime-info {
  --spacer: 2rem;

  .split {
    align-items: center;
  }

  .info {
    flex-basis: 60%;
  }

  .image {
    flex-basis: 40%;
    border-radius: var(--border-radius);
    overflow: hidden;
    margin-right: auto;
  }

  .synopsis {
    height: 15.5rem;
    overflow: auto;

    @supports (scrollbar-color: var(--clr-secondary) var(--clr-accent)) {
      scrollbar-color: var(--clr-secondary) var(--clr-accent);
    }

    @supports (scrollbar-width: thin) {
      scrollbar-width: thin;
    }
  }

  .synopsis::-webkit-scrollbar-track {
    background: var(--clr-accent);
    border-radius: var(--border-radius);
  }

  .synopsis::-webkit-scrollbar {
    width: 8px;
  }
}

